import React from "react";

function Categories() {
  const [currentIndex, setCurrentIndex] = React.useState(0);

  const changeActiveClass = (index) => {
    setCurrentIndex(index);
  };

  return (
    <div className="categories">
      <ul>
        <li
          onClick={() => changeActiveClass(0)}
          className={currentIndex === 0 ? "active" : ""}
        >
          Все
        </li>
        <li
          onClick={() => changeActiveClass(1)}
          className={currentIndex === 1 ? "active" : ""}
        >
          Мясные
        </li>
        <li
          onClick={() => changeActiveClass(2)}
          className={currentIndex === 2 ? "active" : ""}
        >
          Вегетарианская
        </li>
        <li
          onClick={() => changeActiveClass(3)}
          className={currentIndex === 3 ? "active" : ""}
        >
          Гриль
        </li>
        <li
          onClick={() => changeActiveClass(4)}
          className={currentIndex === 4 ? "active" : ""}
        >
          Острые
        </li>
        <li
          onClick={() => changeActiveClass(5)}
          className={currentIndex === 5 ? "active" : ""}
        >
          Закрытые
        </li>
      </ul>
    </div>
  );
}

export default Categories;
