import React from "react";

function Categories() {
  const categories = [
    "Все",
    "Мясные",
    "Вегетарианская",
    "Гриль",
    "Острые",
    "Закрытые",
  ];

  const [currentIndex, setCurrentIndex] = React.useState(0);

  const changeActiveClass = (index) => {
    setCurrentIndex(index);
  };

  return (
    <div className="categories">
      <ul>
        {categories.map((value) => (
          <li
            onClick={() => changeActiveClass(categories.findIndex(value))}
            className={
              currentIndex === categories.findIndex(value) ? "active" : ""
            }
          >
            {value}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Categories;
