import React from "react";

function Categories() {
  const [currentIndex, setCurrentIndex] = React.useState(0);

  // const changeActiveClass = () => {
  //   console.log();
  //   setClassActive();
  // };

  return (
    <div className="categories">
      <ul>
        <li className={currentIndex === 0 ? "active" : ""}>Все</li>
        <li className={currentIndex === 1 ? "active" : ""}>Мясные</li>
        <li className={currentIndex === 2 ? "active" : ""}>Вегетарианская</li>
        <li className={currentIndex === 3 ? "active" : ""}>Гриль</li>
        <li className={currentIndex === 4 ? "active" : ""}>Острые</li>
        <li className={currentIndex === 5 ? "active" : ""}>Закрытые</li>
      </ul>
    </div>
  );
}

export default Categories;
